import React from "react";
import { motion } from "framer-motion";

// Import your images from src/assets/events
import mirudangamImg from "../assets/events/mridungamshow.png";
import veenaImg from "../assets/events/veenashow.png"; // example
import sangeethamImg from "../assets/events/sethupathishow.png"; // example

const events = [
  {
    id: 1,
    title: "‡ÆÆ‡Æø‡Æ∞‡ØÅ‡Æ§‡Æô‡Øç‡Æï‡Æï‡Øç ‡Æï‡Æö‡Øç‡Æö‡Øá‡Æ∞‡Æø",
    description: "‡ÆÖ‡Æ±‡Øç‡Æ™‡ØÅ‡Æ§‡ÆÆ‡Ææ‡Æ© ‡ÆÆ‡Æø‡Æ∞‡ØÅ‡Æ§‡Æô‡Øç‡Æï ‡Æá‡Æö‡Øà ‡Æ®‡Æø‡Æï‡Æ¥‡Øç‡Æö‡Øç‡Æö‡Æø.",
    bgImage: mirudangamImg, // use imported image
  },
  {
    id: 2,
    title: "‡Æµ‡ØÄ‡Æ£‡Øà‡Æï‡Øç ‡Æï‡Æö‡Øç‡Æö‡Øá‡Æ∞‡Æø",
    description: "‡Æö‡Æô‡Øç‡Æï‡ØÄ‡Æ§ ‡Æµ‡Æø‡Æ∞‡ØÅ‡Æ™‡Øç‡Æ™‡Æô‡Øç‡Æï‡Æ≥‡ØÅ‡Æï‡Øç‡Æï‡Ææ‡Æ© ‡Æµ‡ØÄ‡Æ£‡Øà‡Æï‡Øç ‡Æï‡Æö‡Øç‡Æö‡Øá‡Æ∞‡Æø.",
    bgImage: veenaImg,
  },
  {
    id: 3,
    title: "‡Æö‡Øá‡Æ§‡ØÅ‡Æ™‡Æ§‡Æø ‡Æá‡Æö‡Øà ‡Æ®‡Æø‡Æï‡Æ¥‡Øç‡Æö‡Øç‡Æö‡Æø",
    description: "‡Æ®‡Æø‡Æï‡Æ¥‡Øç‡Æö‡Øç‡Æö‡Æø‡ÆØ‡Æø‡Æ©‡Øç ‡ÆÖ‡Æ¥‡Æï‡Ææ‡Æ© ‡Æá‡Æö‡Øà ‡Æ®‡Æø‡Æï‡Æ¥‡Øç‡Æö‡Øç‡Æö‡Æø.",
    bgImage: sangeethamImg,
  },
];

export default function Events() {
  return (
    <section className="py-12 bg-gray-100">
      <div className="container mx-auto px-4">
        <motion.h2
          className="text-3xl font-bold text-center mb-8"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          üéâ ‡Æ®‡Æø‡Æï‡Æ¥‡Øç‡Æµ‡ØÅ‡Æï‡Æ≥‡Øç
        </motion.h2>

        <div className="grid md:grid-cols-3 gap-6">
          {events.map((event) => (
            <motion.div
              key={event.id}
              className="p-6 rounded-lg shadow-lg relative overflow-hidden"
              style={{
                backgroundImage: `url(${event.bgImage})`,
                backgroundSize: "cover",
                backgroundPosition: "center",
                minHeight: "200px",
              }}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5 }}
            >
              {/* Overlay to make text readable */}
              <div className="absolute inset-0 bg-black bg-opacity-40 rounded-lg"></div>

              <motion.div className="relative z-10">
                <motion.h3
                  className="text-xl font-semibold mb-2 text-white"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 0.2 }}
                >
                  {event.title}
                </motion.h3>
                <motion.p
                  className="text-white text-base leading-relaxed"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 0.4 }}
                >
                  {event.description}
                </motion.p>
              </motion.div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
}
