import React, {useEffect, useState} from 'react';
import { getCourses } from '../mockData';
import { motion, useInView, useAnimation } from 'framer-motion';

const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.2
    }
  }
};

const courseVariants = {
  hidden: { 
    opacity: 0,
    y: 50,
    scale: 0.95
  },
  visible: {
    opacity: 1,
    y: 0,
    scale: 1,
    transition: {
      type: "spring",
      stiffness: 100,
      damping: 20,
      duration: 0.6
    }
  },
  hover: {
    y: -10,
    scale: 1.02,
    boxShadow: "0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)",
    transition: {
      type: "spring",
      stiffness: 400,
      damping: 25
    }
  }
};

export default function Courses(){
  const [courses, setCourses] = useState([]);
  useEffect(()=>{ getCourses().then(setCourses) }, []);
  return (
    <section className="mt-10">
      <h2 className="text-2xl font-bold mb-4">படிப்புகள்</h2>
      <div className="grid md:grid-cols-2 gap-6">
        {courses.map(c=>(
          <motion.div key={c.id} className="p-6 bg-white rounded-lg shadow" whileHover={{y:-5}} transition={{type:'spring', stiffness:200}}>
            <h3 className="text-lg font-semibold">{c.title}</h3>
            <p className="mt-2 text-sm">{c.desc}</p>
          </motion.div>
        ))}
      </div>
    </section>
  );
}
