import React, { useEffect, useState } from "react";
import { motion, useReducedMotion } from "framer-motion";
import heroImg from "../assets/team/arun-prakash-I9Q57EFcSAA-unsplash (1).jpg";
import myLogo from "../assets/logo/logo.png";

export default function Hero() {
  const shouldReduceMotion = useReducedMotion();
  const [inViewTrigger, setInViewTrigger] = useState(false);

  useEffect(() => {
    const el = document.getElementById("hero-section");
    if (!el || typeof IntersectionObserver === "undefined") return;

    const io = new IntersectionObserver(
      (entries) => {
        entries.forEach((e) => setInViewTrigger(e.isIntersecting));
      },
      { threshold: 0.1 }
    );

    io.observe(el);
    return () => io.disconnect();
  }, []);

  return (
    <motion.section
      id="hero-section"
      className="relative overflow-hidden min-h-screen flex items-center justify-center"
    >
      {/* ✅ Background image with dark overlay */}
      <motion.div
        className="absolute inset-0"
        initial={{ scale: 1.1 }}
        animate={{ scale: 1 }}
        transition={{ duration: 1.2, ease: "easeOut" }}
      >
        <img
          src={heroImg}
          alt="Hero Background"
          className="w-full h-full object-cover"
        />
        <div className="absolute inset-0 bg-gradient-to-b from-black/60 via-black/40 to-black/70" />
      </motion.div>

      {/* ✅ Logo at top-left */}
      <motion.div
        className="absolute top-4 left-4 sm:top-6 sm:left-6 md:top-8 md:left-8 z-50"
        initial={{ opacity: 0, x: -6 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.6 }}
      >
        <img
          src={myLogo}
          alt="Logo"
          className="w-16 sm:w-20 md:w-28 lg:w-36 h-auto drop-shadow-xl bg-white/10 p-2 rounded-lg backdrop-blur-sm"
          style={{ display: "block" }}
        />
      </motion.div>

      {/* ✅ Hero Content */}
      <div className="relative z-10 text-center text-white px-4">
        {/* Tamil Title with floating animation */}
        <motion.h1
          className="text-6xl md:text-8xl font-extrabold mb-6 tracking-wide"
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          <motion.span
            animate={{ y: [0, -10, 0] }}
            transition={{ duration: 4, repeat: Infinity, ease: "easeInOut" }}
            className="inline-block text-transparent bg-clip-text bg-gradient-to-r from-[#00bfa5] to-[#7ef9e3] drop-shadow-md"
          >
            லயரிதம்
          </motion.span>
        </motion.h1>

        {/* Subtitle */}
        <motion.p
          className="text-lg md:text-2xl text-gray-200 mb-8 max-w-2xl mx-auto leading-relaxed"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.3 }}
        >
          இசையின் உலகில் உங்கள் பயணத்தை தொடங்குங்கள். அனுபவமிக்க ஆசிரியர்களுடன்
          உங்கள் திறமைகளை வளர்த்தெடுக்கலாம்.
        </motion.p>

        {/* Call-to-Action Button */}
        <motion.a
          href="#"
          className="inline-block bg-[#00bfa5] hover:bg-[#009e8b] text-white text-xl px-10 py-4 rounded-full font-semibold shadow-lg"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          transition={{ duration: 0.2 }}
        >
          இப்போது விண்ணப்பிக்கவும்
        </motion.a>
      </div>

      {/* ✅ Floating musical icons */}
      {!shouldReduceMotion && (
        <>
          <motion.div
            className="absolute bottom-16 left-20 text-white/40 text-4xl"
            animate={{ y: [0, -20, 0], opacity: [0.7, 1, 0.7] }}
            transition={{ duration: 4, repeat: Infinity, ease: "easeInOut" }}
          >
            ♪
          </motion.div>
          <motion.div
            className="absolute top-24 right-24 text-white/40 text-4xl"
            animate={{ y: [0, -20, 0], opacity: [0.7, 1, 0.7] }}
            transition={{
              duration: 5,
              repeat: Infinity,
              ease: "easeInOut",
              delay: 1.5,
            }}
          >
            ♫
          </motion.div>
        </>
      )}
    </motion.section>
  );
}
