import React, { useEffect, useState, useRef } from "react";
import { getCourses } from "../mockData";
import {
  motion,
  useInView,
  useAnimation,
  useScroll,
  useTransform
} from "framer-motion";
import bgImage from "../assets/bg/courses-bg.jpg"; // <- update path if needed

const containerVariants = {
  hidden: { opacity: 0 },
  visible: { opacity: 1, transition: { staggerChildren: 0.12 } }
};

const courseVariants = {
  hidden: { opacity: 0, y: 40, scale: 0.98 },
  visible: {
    opacity: 1,
    y: 0,
    scale: 1,
    transition: { type: "spring", stiffness: 110, damping: 20, duration: 0.6 }
  },
  hover: {
    y: -8,
    scale: 1.02,
    boxShadow:
      "0 20px 25px -5px rgba(0,0,0,0.1), 0 10px 10px -5px rgba(0,0,0,0.04)",
    transition: { type: "spring", stiffness: 400, damping: 25 }
  }
};

export default function Courses() {
  const [courses, setCourses] = useState([]);
  const ref = useRef(null);

  // in-view + animation controls for cards
  const controls = useAnimation();
  const inView = useInView(ref, { threshold: 0.18 });

  useEffect(() => {
    if (inView) controls.start("visible");
    else controls.start("hidden");
  }, [controls, inView]);

  // targetted scroll progress for parallax (works only when element is in viewport)
  const { scrollYProgress } = useScroll({
    target: ref,
    offset: ["start end", "end start"] // when section enters -> 0, when it leaves -> 1
  });

  // translate progress to pixel offset for background.
  // tweak [-80, -160] to increase/decrease parallax distance
  const backgroundY = useTransform(scrollYProgress, [0, 1], [0, -120]);

  // load courses
  useEffect(() => {
    let mounted = true;
    getCourses().then((data) => {
      if (mounted) setCourses(data || []);
    });
    return () => {
      mounted = false;
    };
  }, []);

  return (
    <section
      ref={ref}
      className="relative overflow-hidden"
      style={{ padding: "80px 1rem" }}
      aria-label="படிப்புகள்"
    >
      {/* Parallax background - using motion so y transforms animate smoothly */}
      <motion.div
        className="absolute inset-0 bg-cover bg-center pointer-events-none"
        style={{
          backgroundImage: `url(${bgImage})`,
          y: backgroundY
        }}
        aria-hidden
      />

      {/* overlay for readability */}
      <div className="absolute inset-0 bg-black/40 pointer-events-none" />

      {/* content container */}
      <div className="relative z-10 max-w-6xl mx-auto">
        <motion.h2
          className="text-3xl md:text-4xl font-bold mb-8 text-center text-white"
          initial={{ opacity: 0, y: 12 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          படிப்புகள்
        </motion.h2>

        <motion.div
          className="grid gap-8 md:grid-cols-2"
          variants={containerVariants}
          initial="hidden"
          animate={controls}
        >
          {courses.length === 0 ? (
            // graceful fallback while loading or if no courses
            <div className="col-span-full text-center text-white/90">
              ஏதுமில்லை — படிப்புகள் வந்து கொண்டிருக்கின்றன...
            </div>
          ) : (
            courses.map((c, i) => (
              <motion.article
                key={c.id ?? i}
                className="p-6 bg-white/95 rounded-lg shadow-md"
                variants={courseVariants}
                whileHover="hover"
                whileTap={{ scale: 0.98 }}
              >
                <motion.h3
                  className="text-xl font-semibold"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: i * 0.06 }}
                >
                  {c.title}
                </motion.h3>

                <motion.p
                  className="mt-3 text-gray-700"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: i * 0.06 + 0.14 }}
                >
                  {c.desc}
                </motion.p>

                <motion.div
                  className="mt-4 flex justify-end"
                  initial={{ opacity: 0, x: -12 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: i * 0.06 + 0.28 }}
                >
                  <motion.button
                    className="px-4 py-2 bg-primary text-white rounded-lg hover:bg-primary/90 transition-colors"
                    whileHover={{ scale: 1.04 }}
                    whileTap={{ scale: 0.96 }}
                  >
                    மேலும் அறிக
                  </motion.button>
                </motion.div>
              </motion.article>
            ))
          )}
        </motion.div>
      </div>
    </section>
  );
}
