import React from "react";
import { motion } from "framer-motion";
import Lottie from "lottie-react";
import FeatureCard from "./FeatureCard";
import musicNotes from "../assets/music-notes.json";

export default function Features() {
  return (
    <section className="relative overflow-hidden bg-gradient-to-br from-pink-50 via-white to-yellow-50 py-20">
      {/* Background Lottie animation */}
      <div className="absolute left-0 top-1/2 transform -translate-y-1/2 opacity-40 w-72 md:w-96 pointer-events-none">
        <Lottie animationData={musicNotes} loop={true} />
      </div>

      <div className="container mx-auto px-4 relative z-10">
        {/* Heading Section */}
        <motion.div
          initial={{ opacity: 0, y: -30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="max-w-4xl mx-auto text-center mb-12"
        >
          <h2 className="text-4xl md:text-5xl font-extrabold text-gray-800 mb-4 drop-shadow-sm">
            üé∂ ‡Æé‡Æ§‡Øà‡Æï‡Øç ‡Æï‡Æ±‡Øç‡Æ™‡Æ§‡ØÅ?
          </h2>
          <p className="text-lg text-gray-700 leading-relaxed">
            ‡ÆÖ‡Æ©‡ØÅ‡Æ™‡Æµ‡ÆÆ‡Æø‡Æï‡Øç‡Æï ‡ÆÜ‡Æö‡Æø‡Æ∞‡Æø‡ÆØ‡Æ∞‡Øç‡Æï‡Æ≥‡Øç, ‡Æ®‡Øá‡Æ∞‡Æ≤‡Øà ‡Æµ‡Æï‡ØÅ‡Æ™‡Øç‡Æ™‡ØÅ‡Æï‡Æ≥‡Øç ‡ÆÆ‡Æ±‡Øç‡Æ±‡ØÅ‡ÆÆ‡Øç ‡Æ®‡Æµ‡ØÄ‡Æ© ‡Æ™‡Ææ‡Æü‡Æ§‡Øç‡Æ§‡Æø‡Æü‡Øç‡Æü‡Æô‡Øç‡Æï‡Æ≥‡Ææ‡Æ≤‡Øç
            ‡ÆÜ‡Æ© ‡Æí‡Æ∞‡ØÅ‡Æô‡Øç‡Æï‡Æø‡Æ£‡Øà‡Æ®‡Øç‡Æ§ ‡Æ™‡Ææ‡Æü‡Æ®‡ØÜ‡Æ±‡Æø‡Æï‡Æ≥‡Øç.
          </p>
        </motion.div>

        {/* Feature Cards Section */}
        <div className="grid gap-10 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3">
          {[
            {
              icon: "üéì",
              title: "‡ÆÖ‡Æ©‡ØÅ‡Æ™‡Æµ‡ÆÆ‡Øç ‡Æµ‡Ææ‡ÆØ‡Øç‡Æ®‡Øç‡Æ§ ‡ÆÜ‡Æö‡Æø‡Æ∞‡Æø‡ÆØ‡Æ∞‡Øç‡Æï‡Æ≥‡Øç",
              text: "‡Æ™‡Ææ‡Æ∞‡ÆÆ‡Øç‡Æ™‡Æ∞‡Æø‡ÆØ‡ÆÆ‡Øç ‡ÆÆ‡Æ±‡Øç‡Æ±‡ØÅ‡ÆÆ‡Øç ‡Æ®‡ØÅ‡Æü‡Øç‡Æ™‡Æô‡Øç‡Æï‡Æ≥‡Øà ‡Æâ‡Æü‡Øà‡ÆØ ‡Æï‡Æ≤‡Øà‡Æû‡Æ∞‡Øç‡Æï‡Æ≥‡Øç ‡ÆÆ‡ØÇ‡Æ≤‡ÆÆ‡Øç ‡Æ™‡ÆØ‡Æø‡Æ±‡Øç‡Æö‡Æø.",
            },
            {
              icon: "üéõÔ∏è",
              title: "‡Æ®‡Øá‡Æ∞‡Æ≤‡Øà & ‡Æ®‡Øá‡Æ∞‡Æü‡Æø ‡Æµ‡Æï‡ØÅ‡Æ™‡Øç‡Æ™‡ØÅ‡Æï‡Æ≥‡Øç",
              text: "‡Æ®‡Øá‡Æ∞‡Æü‡Æø ‡Æá‡Æü‡Øà‡ÆØ‡ØÇ‡Æ±‡ØÅ ‡Æá‡Æ≤‡Øç‡Æ≤‡Ææ‡ÆÆ‡Æ≤‡Øç ‡Æ®‡Øá‡Æ∞‡Æ≤‡Øà ‡Æµ‡Æï‡ØÅ‡Æ™‡Øç‡Æ™‡ØÅ‡Æï‡Æ≥‡Øç ‡ÆÆ‡Æ±‡Øç‡Æ±‡ØÅ‡ÆÆ‡Øç ‡Æ™‡Æ§‡Æø‡Æµ‡ØÅ ‡Æö‡ØÜ‡ÆØ‡Øç‡ÆØ‡Æ™‡Øç‡Æ™‡Æü‡Øç‡Æü ‡Æ™‡Ææ‡Æü‡Æô‡Øç‡Æï‡Æ≥‡Øç.",
            },
            {
              icon: "üìö",
              title: "‡Æ®‡Æµ‡ØÄ‡Æ© ‡Æ™‡Ææ‡Æü‡Æ§‡Øç‡Æ§‡Æø‡Æü‡Øç‡Æü‡ÆÆ‡Øç",
              text: "‡Æµ‡Æ≥‡Æ∞‡Øç‡Æö‡Øç‡Æö‡Æø ‡ÆÆ‡Øà‡ÆØ‡ÆÆ‡Ææ‡Æ© ‡Æ™‡Ææ‡Æü‡Æô‡Øç‡Æï‡Æ≥‡Øç, ‡Æ®‡Æü‡Øà‡ÆÆ‡ØÅ‡Æ±‡Øà ‡ÆÖ‡Æ©‡ØÅ‡Æ™‡Æµ‡ÆÆ‡Øç ‡ÆÆ‡Æ±‡Øç‡Æ±‡ØÅ‡ÆÆ‡Øç ‡Æ§‡Øá‡Æ∞‡Øç‡Æö‡Øç‡Æö‡Æø ‡ÆÖ‡Æü‡Øà‡Æ™‡Øç‡Æ™‡ØÅ ‡Æ®‡Øã‡Æï‡Øç‡Æï‡Æô‡Øç‡Æï‡Æ≥‡Øç.",
            },
          ].map((item, idx) => (
            <motion.div
              key={idx}
              initial={{ opacity: 0, y: 40 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ delay: idx * 0.2, duration: 0.7 }}
              viewport={{ once: true }}
            >
              <FeatureCard icon={item.icon} title={item.title}>
                {item.text}
              </FeatureCard>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
}
