import React, {useEffect, useState} from 'react';
import { getCourses } from '../mockData';
import { motion } from 'framer-motion';

export default function Courses(){
  const [courses, setCourses] = useState([]);
  useEffect(()=>{ getCourses().then(setCourses) }, []);
  return (
    <section className="mt-10">
      <h2 className="text-2xl font-bold mb-4">படிப்புகள்</h2>
      <div className="grid md:grid-cols-2 gap-6">
        {courses.map(c=>(
          <motion.div key={c.id} className="p-6 bg-white rounded-lg shadow" whileHover={{y:-5}} transition={{type:'spring', stiffness:200}}>
            <h3 className="text-lg font-semibold">{c.title}</h3>
            <p className="mt-2 text-sm">{c.desc}</p>
          </motion.div>
        ))}
      </div>
    </section>
  );
}
