import React, { useEffect, useState, useRef } from "react";
import { getCourses } from "../mockData";
import {
  motion,
  useAnimation,
  useInView,
  useScroll,
  useTransform
} from "framer-motion";
import bgImage from "../assets/bg/paralelcourse.jpg"; // ✅ background image

const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: { staggerChildren: 0.2 },
  },
};

const courseVariants = {
  hidden: { opacity: 0, y: 50, scale: 0.95 },
  visible: {
    opacity: 1,
    y: 0,
    scale: 1,
    transition: {
      type: "spring",
      stiffness: 100,
      damping: 20,
      duration: 0.6,
    },
  },
  hover: {
    y: -10,
    scale: 1.02,
    boxShadow:
      "0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)",
    transition: { type: "spring", stiffness: 400, damping: 25 },
  },
};

export default function Courses() {
  const [courses, setCourses] = useState([]);
  const controls = useAnimation();
  const ref = useRef(null);
  const inView = useInView(ref, { threshold: 0.2 });

  // ✅ Proper scroll-based parallax setup
  const { scrollYProgress } = useScroll({
    target: ref,
    offset: ["start end", "end start"], // section start to end
  });

  // Background parallax motion
  const backgroundY = useTransform(scrollYProgress, [0, 1], ["0%", "-25%"]);

  // Slight parallax for cards (moves opposite direction)
  const cardY = useTransform(scrollYProgress, [0, 1], ["0%", "10%"]);

  useEffect(() => {
    if (inView) controls.start("visible");
  }, [controls, inView]);

  useEffect(() => {
    getCourses().then(setCourses);
  }, []);

  return (
    <section
      ref={ref}
      className="mt-10 relative overflow-hidden"
      style={{ minHeight: "100vh" }}
    >
      {/* ✅ Parallax Background */}
      <motion.div
        className="absolute inset-0 bg-cover bg-center"
        style={{
          backgroundImage: `url(${bgImage})`,
          y: backgroundY,
        }}
      ></motion.div>

      {/* Dark overlay for readability */}
      <div className="absolute inset-0 bg-black/40"></div>

      {/* Content */}
      <motion.div
        className="relative z-10 px-6 py-20 max-w-6xl mx-auto"
        variants={containerVariants}
        initial="hidden"
        animate={controls}
      >
        <motion.h2
          className="text-3xl md:text-4xl font-bold mb-10 text-center text-white"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          படிப்புகள்
        </motion.h2>

        {/* ✅ Cards with slight parallax and animations */}
        <motion.div
          className="grid md:grid-cols-2 gap-8"
          style={{ y: cardY }}
          variants={containerVariants}
        >
          {courses.map((c, index) => (
            <motion.div
              key={c.id}
              className="p-6 bg-white/90 rounded-lg shadow-md overflow-hidden backdrop-blur-sm"
              variants={courseVariants}
              whileHover="hover"
              whileTap={{ scale: 0.98 }}
            >
              <motion.h3
                className="text-xl font-semibold"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: index * 0.1 }}
              >
                {c.title}
              </motion.h3>
              <motion.p
                className="mt-3 text-gray-700"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: index * 0.1 + 0.2 }}
              >
                {c.desc}
              </motion.p>
              <motion.div
                className="mt-4 flex justify-end"
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: index * 0.1 + 0.4 }}
              >
                <motion.button
                  className="px-4 py-2 bg-primary text-white rounded-lg hover:bg-primary/90 transition-colors"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  மேலும் அறிக
                </motion.button>
              </motion.div>
            </motion.div>
          ))}
        </motion.div>
      </motion.div>
    </section>
  );
}
