import React, { useEffect, useState } from 'react';
import { motion, useReducedMotion, useScroll, useTransform, AnimatePresence } from 'framer-motion';
import heroImg from '../assets/team/arun-prakash-I9Q57EFcSAA-unsplash (1).jpg';
import myLogo from '../assets/logo/logo.png';

export default function Hero() {
  const shouldReduceMotion = useReducedMotion();
  const [inViewTrigger, setInViewTrigger] = useState(false);

  useEffect(() => {
    // Simple IntersectionObserver to detect when hero leaves the viewport
    const el = document.getElementById('hero-section');
    if (!el || typeof IntersectionObserver === 'undefined') return;

    const io = new IntersectionObserver(
      (entries) => {
        entries.forEach((e) => {
          setInViewTrigger(e.isIntersecting);
        });
      },
      { threshold: 0.1 }
    );

    io.observe(el);
    return () => io.disconnect();
  }, []);

  const container = {
    hidden: { opacity: 1 },
    show: { transition: { staggerChildren: 0.12 } },
  };

  const item = {
    hidden: { opacity: 0, y: 12 },
    show: { opacity: 1, y: 0, transition: { duration: 0.5, ease: 'easeOut' } },
  };

  // musical note animation (floats up and fades)
  const noteAnim = shouldReduceMotion
    ? {}
    : {
        initial: { opacity: 0, y: 20, rotate: -10 },
        animate: { opacity: [0.8, 0.5, 0], y: [20, -40], rotate: [0, 8, 0] },
        transition: { duration: 4, repeat: Infinity, ease: 'easeInOut' },
      };

  return (
    <motion.section id="hero-section" className="relative overflow-hidden min-h-screen">
      <div className="absolute inset-0">
        <motion.img
          src={heroImg}
          alt="background"
          className="w-full h-full object-cover"
          initial={{ scale: 1.1 }}
          animate={{ scale: 1 }}
          transition={{ duration: 0.8 }}
        />
        <div className="absolute inset-0 bg-black/50" />
      </div>

      <div style={{backgroundColor: 'rgba(0,0,0,0.5)'}} className="relative h-screen flex flex-col items-center justify-center text-center text-white px-4">
        {/* ✅ Logo positioned at top right */}
        <motion.div
          className="absolute top-8 right-8 w-32 md:w-40 lg:w-48"
          initial={{ scale: 0.9, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
          <img
            src={myLogo}
            alt="Logo"
            className="w-full h-auto drop-shadow-lg bg-white/10 p-2 rounded-lg"
          />
        </motion.div>

        <motion.div
          initial={{ y: 20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.6 }}
          className="max-w-4xl mx-auto"
        >
          <motion.h1
            className="text-6xl md:text-8xl font-bold mb-12"
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.6, delay: 0.2 }}
          >
            <motion.span
              aria-hidden={shouldReduceMotion}
              animate={shouldReduceMotion ? undefined : { y: [0, -8, 0] }}
              transition={{
                repeat: shouldReduceMotion ? 0 : Infinity,
                duration: 4,
                ease: 'easeInOut',
                delay: 0.6,
              }}
              className="inline-block"
            >
              லயரிதம்
            </motion.span>
          </motion.h1>

          <motion.div
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.6, delay: 0.4 }}
            className="flex justify-center"
          >
            <motion.a
              href="#"
              className="inline-block bg-[#00bfa5] hover:bg-[#00a392] text-white text-xl px-12 py-4 rounded-full font-medium tracking-wide"
              whileHover={{ scale: 1.03, backgroundColor: '#00a392' }}
              whileTap={{ scale: 0.98 }}
              transition={{ duration: 0.2 }}
            >
              இப்போது விண்ணப்பிக்கவும்
            </motion.a>
          </motion.div>
        </motion.div>

        {/* Floating musical notes */}
        {!shouldReduceMotion && (
          <>
            <motion.div
              className="absolute -top-8 left-1/4 text-white/60 text-4xl"
              {...noteAnim}
              style={{ pointerEvents: 'none' }}
            >
              ♪
            </motion.div>
            <motion.div
              className="absolute -bottom-8 right-1/4 text-white/60 text-4xl"
              {...noteAnim}
              transition={{ ...noteAnim.transition, delay: 1.2 }}
              style={{ pointerEvents: 'none' }}
            >
              ♫
            </motion.div>
          </>
        )}
      </div>
    </motion.section>
  );
}
