            href="#features"
            className="inline-block bg-[#00bfa5] hover:bg-[#009e8b] text-white text-xl px-10 py-4 rounded-full font-semibold shadow-lg"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            transition={{ duration: 0.2 }}
