import React, { useEffect, useState } from 'react';
import { motion, useReducedMotion } from 'framer-motion';
import heroImg from '../assets/hero.jpg';

export default function Hero(){
  const shouldReduceMotion = useReducedMotion();
  const [inViewTrigger, setInViewTrigger] = useState(false);

  useEffect(() => {
    // Simple IntersectionObserver to detect when hero leaves the viewport — used to trigger an "end" effect
    const el = document.getElementById('hero-section');
    if (!el || typeof IntersectionObserver === 'undefined') return;

    const io = new IntersectionObserver((entries) => {
      entries.forEach(e => {
        // when hero is not fully visible (scrolled past), trigger the end animation
        setInViewTrigger(e.isIntersecting);
      });
    }, { threshold: 0.1 });

    io.observe(el);
    return () => io.disconnect();
  }, []);

  const container = {
    hidden: { opacity: 1 },
    show: { transition: { staggerChildren: 0.12 } }
  };

  const item = {
    hidden: { opacity: 0, y: 12 },
    show: { opacity: 1, y: 0, transition: { duration: 0.5, ease: 'easeOut' } }
  };

  // musical note animation (floats up and fades)
  const noteAnim = shouldReduceMotion ? {} : {
    initial: { opacity: 0, y: 20, rotate: -10 },
    animate: { opacity: [0.8, 0.5, 0], y: [20, -40], rotate: [0, 8, 0] },
    transition: { duration: 4, repeat: Infinity, ease: 'easeInOut' }
  };

  return (
    <motion.section id="hero-section" className="relative overflow-hidden">
      <div className="h-80 md:h-96 bg-gradient-to-r from-primary/90 to-indigo-700 flex items-center">
        <div className="container mx-auto px-4 text-white flex flex-col md:flex-row items-center md:items-start gap-6">
          <div className="md:w-1/2">
            {/* Floating name: entrance + gentle bobbing */}
            <motion.div initial={{ x: -160, opacity: 0 }} animate={{ x: 0, opacity: 1 }} transition={{ type: 'spring', stiffness: 80, damping: 14 }}>
              <motion.h1 className="text-3xl md:text-5xl font-extrabold inline-block leading-tight">
                <motion.span
                  aria-hidden={shouldReduceMotion}
                  animate={shouldReduceMotion ? undefined : { y: [0, -8, 0] }}
                  transition={{ repeat: shouldReduceMotion ? 0 : Infinity, duration: 4, ease: 'easeInOut', delay: 0.6 }}
                >
                  லயரிதம்
                </motion.span>
              </motion.h1>
            </motion.div>

            <motion.div variants={container} initial="hidden" animate="show">
              <motion.p variants={item} className="mt-4 max-w-xl">கர்நாடக இசையின் அறிமுகம் — அனுபவம் வாய்ந்த ஆசிரியர்களுடன் நேரடி வகுப்புகள் மற்றும் புதிய பாடத்திட்டம்.</motion.p>

              <motion.div variants={item} className="mt-6 flex flex-wrap gap-3">
                <motion.a whileHover={{ scale: 1.03 }} whileTap={{ scale: 0.98 }} className="inline-block bg-white text-primary font-semibold px-6 py-2 rounded shadow" href="#">இப்போது விண்ணப்பிக்கவும்</motion.a>
                <motion.a whileHover={{ scale: 1.03 }} whileTap={{ scale: 0.98 }} className="inline-block border border-white text-white px-6 py-2 rounded hover:bg-white/10" href="#">நிகழ்வுகளை பார்க்க</motion.a>
              </motion.div>
            </motion.div>
          </div>

          <div className="md:w-1/2 flex justify-center md:justify-end relative">
            {/* hero image */}
            <motion.img src={heroImg} alt="hero" className="hidden sm:block w-64 h-48 object-cover rounded-lg shadow-lg border-4 border-white/30" initial={{ scale: 0.9, opacity: 0 }} animate={{ scale: 1, opacity: 1 }} transition={{ duration: 0.6, delay: 0.3 }} />

            {/* floating musical notes — only animate while hero is in view and when motion not reduced */}
            {!shouldReduceMotion && (
              <>
                <motion.div className="absolute -bottom-4 left-8 text-white/90 text-2xl" {...noteAnim} style={{ pointerEvents: 'none' }} />
                <motion.div className="absolute -bottom-6 left-24 text-white/70 text-3xl" {...noteAnim} transition={{ ...noteAnim.transition, delay: 0.8 }} style={{ pointerEvents: 'none' }}>
                  ♪
                </motion.div>
                <motion.div className="absolute -bottom-2 right-12 text-white/60 text-2xl" {...noteAnim} transition={{ ...noteAnim.transition, delay: 1.6 }} style={{ pointerEvents: 'none' }}>
                  ♫
                </motion.div>
              </>
            )}
          </div>
        </div>
      </div>

      {/* subtle end-state overlay: fades in when hero is scrolled out (inViewTrigger === false) */}
      <motion.div aria-hidden className="absolute inset-0 pointer-events-none" initial={{ opacity: 0 }} animate={{ opacity: inViewTrigger ? 0 : 0.08 }} transition={{ duration: 0.5 }} style={{ background: 'linear-gradient(180deg, transparent, rgba(0,0,0,0.06))' }} />
    </motion.section>
  );
}
