import React, { useEffect, useState, useRef } from 'react';
import { getEvents } from '../mockData';
import { motion, useInView, useAnimation } from 'framer-motion';
import axios from 'axios';

const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.15,
      delayChildren: 0.2
    }
  }
};

const eventCardVariants = {
  hidden: { 
    opacity: 0,
    y: 80,
    scale: 0.85
  },
  visible: {
    opacity: 1,
    y: 0,
    scale: 1,
    transition: {
      type: "spring",
      stiffness: 90,
      damping: 18
    }
  },
  hover: {
    scale: 1.07,
    y: -12,
    boxShadow: "0 20px 40px rgba(0, 0, 0, 0.15)",
    transition: {
      type: "spring",
      stiffness: 350,
      damping: 25
    }
  }
};

export default function Events() {
  const [events, setEvents] = useState([]);
  const controls = useAnimation();
  const ref = useRef(null);
  const inView = useInView(ref, { threshold: 0.1 });

  useEffect(() => {
    if (inView) controls.start("visible");
  }, [controls, inView]);

  useEffect(() => {
    getEvents().then(setEvents);
  }, []);

  return (
    <section className="mt-16 overflow-hidden px-6" ref={ref}>
      <motion.h2 
        className="text-4xl font-extrabold mb-10 text-center text-gray-800"
        initial={{ opacity: 0, y: 30 }}
        animate={inView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
      >
        ‡Æ®‡Æø‡Æï‡Æ¥‡Øç‡Æµ‡ØÅ‡Æï‡Æ≥‡Øç
      </motion.h2>

      <motion.div 
        className="grid sm:grid-cols-2 lg:grid-cols-3 gap-10"
        variants={containerVariants}
        initial="hidden"
        animate={controls}
      >
        {events.map((ev, index) => (
          <motion.div 
            key={ev.id} 
            className="relative bg-white rounded-2xl shadow-lg p-8 min-h-[340px] hover:shadow-2xl transition-all duration-300 overflow-hidden"
            variants={eventCardVariants}
            whileHover="hover"
            whileTap={{ scale: 0.97 }}
          >
            {/* Left Accent Bar */}
            <motion.div
              className="absolute top-0 left-0 w-2 h-full bg-gradient-to-b from-primary to-indigo-500 rounded-l-2xl"
              initial={{ scaleY: 0 }}
              animate={{ scaleY: 1 }}
              transition={{ delay: index * 0.1, duration: 0.6 }}
            />

            {‡ÆÖ‡Æ∞‡Æô‡Øç‡Æï‡Øá‡Æ±‡Øç‡Æ±‡ÆÆ‡Øç}
            <motion.h4 
              className="font-semibold text-xl mb-3 text-gray-900"
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: index * 0.1 + 0.3 }}
            >
              {ev.title}
            </motion.h4>

            {/* Date */}
            <motion.div 
              className="text-sm text-primary font-semibold mb-3"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: index * 0.1 + 0.4 }}
            >
              üìÖ {ev.date}
            </motion.div>

            {/* Description */}
            <motion.p 
              className="text-gray-600 text-base leading-relaxed"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: index * 0.1 + 0.5 }}
            >
              {ev.desc}
            </motion.p>

            {/* Button */}
            <motion.div
              className="mt-6 flex justify-end"
              initial={{ opacity: 0, y: 25 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 + 0.6 }}
            >
              <motion.button
                className="px-5 py-2.5 bg-primary/10 text-primary font-medium rounded-lg hover:bg-primary/20 transition-colors text-sm"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.95 }}
              >
                ‡ÆÆ‡Øá‡Æ≤‡ØÅ‡ÆÆ‡Øç ‡ÆÖ‡Æ±‡Æø‡Æï ‚Üí
              </motion.button>
            </motion.div>
          </motion.div>
        ))}
      </motion.div>
    </section>
  );
}
