import React, {useEffect, useState} from 'react';
import { getEvents } from '../mockData';
import { motion } from 'framer-motion';
import axios from 'axios';

export default function Events(){
  const [events, setEvents] = useState([]);
  useEffect(()=>{ getEvents().then(setEvents) }, []);
  return (
    <section className="mt-12">
      <h2 className="text-2xl font-bold mb-4">நிகழ்வுகள்</h2>
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {events.map(ev=>(
          <motion.div key={ev.id} className="p-4 bg-white rounded-lg shadow" whileHover={{scale:1.02}}>
            <h4 className="font-semibold">{ev.title}</h4>
            <div className="text-xs text-gray-500">{ev.date}</div>
            <p className="mt-2 text-sm">{ev.desc}</p>
          </motion.div>
        ))}
      </div>
    </section>
  );
}
