import React, { useEffect, useState, useRef } from 'react';
import { getCourses } from '../mockData';
import { motion, useAnimation, useInView, useMotionValue, useTransform } from 'framer-motion';
import bgImage from '../assets/bg/paralelcourse.jpg'; // Your background image

const containerVariants = {
  hidden: { opacity: 0 },
  visible: { 
    opacity: 1,
    transition: { staggerChildren: 0.2 }
  }
};

const courseVariants = {
  hidden: { opacity: 0, y: 50, scale: 0.95 },
  visible: { 
    opacity: 1, y: 0, scale: 1,
    transition: { type: "spring", stiffness: 100, damping: 20, duration: 0.6 }
  },
  hover: {
    y: -10,
    scale: 1.03,
    boxShadow: "0 25px 30px -10px rgba(0,0,0,0.15)",
    transition: { type: "spring", stiffness: 400, damping: 25 }
  }
};

export default function Courses() {
  const [courses, setCourses] = useState([]);
  const controls = useAnimation();
  const ref = useRef(null);
  const inView = useInView(ref, { threshold: 0.2 });

  // Parallax motion
  const scrollY = useMotionValue(0);
  const backgroundY = useTransform(scrollY, [0, 1000], [0, -200]);

  useEffect(() => {
    if (inView) controls.start("visible");
  }, [controls, inView]);

  useEffect(() => {
    getCourses().then(setCourses);

    const handleScroll = () => scrollY.set(window.scrollY);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section
      ref={ref}
      className="mt-10 overflow-hidden relative"
      style={{ height: '100vh' }}
    >
      {/* ✅ Parallax Background (top-aligned) */}
      <motion.div
        className="absolute inset-0 bg-no-repeat bg-cover"
        style={{
          backgroundImage: `url(${bgImage})`,
          backgroundPosition: 'top center', // <-- shows top part first
          y: backgroundY,
          scale: 1.1
        }}
      ></motion.div>

      {/* Gentle overlay for readability */}
      <div className="absolute inset-0 bg-gradient-to-b from-white/10 via-white/40 to-white/70"></div>

      {/* Content */}
      <motion.div className="relative z-10 px-6 py-20">
        <motion.h2
          className="text-4xl font-bold mb-10 text-center text-gray-900 drop-shadow-md"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          படிப்புகள்
        </motion.h2>

        <motion.div
          className="grid md:grid-cols-2 lg:grid-cols-3 gap-10"
          variants={containerVariants}
          initial="hidden"
          animate={controls}
        >
          {courses.map((c, index) => (
            <motion.div
              key={c.id}
              className="p-6 bg-white/90 backdrop-blur-md rounded-2xl shadow-xl border border-white/50"
              variants={courseVariants}
              whileHover="hover"
              whileTap={{ scale: 0.98 }}
            >
              <motion.h3
                className="text-xl font-semibold text-gray-900"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: index * 0.1 }}
              >
                {c.title}
              </motion.h3>
              <motion.p
                className="mt-3 text-gray-700"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: index * 0.1 + 0.2 }}
              >
                {c.desc}
              </motion.p>
              <motion.div
                className="mt-4 flex justify-end"
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: index * 0.1 + 0.4 }}
              >
                <motion.button
                  className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  மேலும் அறிக
                </motion.button>
              </motion.div>
            </motion.div>
          ))}
        </motion.div>
      </motion.div>
    </section>
  );
}
