import React from 'react';
import { motion } from 'framer-motion';
import heroImg from '../assets/hero.jpg';

export default function Hero(){
  const container = {
    hidden: { opacity: 1 },
    show: { transition: { staggerChildren: 0.12 } }
  };

  const item = {
    hidden: { opacity: 0, y: 12 },
    show: { opacity: 1, y: 0, transition: { duration: 0.5, ease: 'easeOut' } }
  };

  return (
    <section className="relative">
      <div className="h-80 md:h-96 bg-gradient-to-r from-primary/90 to-indigo-700 flex items-center">
        <div className="container mx-auto px-4 text-white flex flex-col md:flex-row items-center md:items-start gap-6">
          <div className="md:w-1/2">
            {/* Floating name: entrance + gentle bobbing */}
            <motion.div initial={{ x: -160, opacity: 0 }} animate={{ x: 0, opacity: 1 }} transition={{ type: 'spring', stiffness: 80, damping: 14 }}>
              <motion.h1 className="text-3xl md:text-5xl font-extrabold inline-block">
                <motion.span animate={{ y: [0, -6, 0] }} transition={{ repeat: Infinity, repeatType: 'loop', duration: 4, ease: 'easeInOut', delay: 0.6 }}>
                  லயரிதம்
                </motion.span>
              </motion.h1>
            </motion.div>

            <motion.div variants={container} initial="hidden" animate="show">
              <motion.p variants={item} className="mt-4 max-w-xl">கரநாடக இசையின் அறிமுகம் — அனுபவம் வாய்ந்த ஆசிரியர்களுடன் நேரடி வகுப்புகள் மற்றும் புதிய பாடத்திட்டம்.</motion.p>

              <motion.div variants={item} className="mt-6 flex flex-wrap gap-3">
                <a className="inline-block bg-white text-primary font-semibold px-6 py-2 rounded shadow hover:scale-105 transition-transform" href="#">இப்போது விண்ணப்பிக்கவும்</a>
                <a className="inline-block border border-white text-white px-6 py-2 rounded hover:bg-white/10" href="#">நிகழ்வுகளை பார்க்க</a>
              </motion.div>
            </motion.div>
          </div>

          <div className="md:w-1/2 flex justify-center md:justify-end">
            {/* placeholder image or hero image */}
            <motion.img src={heroImg} alt="hero" className="hidden sm:block w-64 h-48 object-cover rounded-lg shadow-lg border-4 border-white/30" initial={{ scale: 0.9, opacity: 0 }} animate={{ scale: 1, opacity: 1 }} transition={{ duration: 0.6, delay: 0.3 }} />
          </div>
        </div>
      </div>
    </section>
  );
}
