import React, { useEffect, useState, useRef } from "react";
import { getCourses } from "../mockData";
import {
  motion,
  useAnimation,
  useInView,
  useScroll,
  useTransform,
} from "framer-motion";
import bgImage from "../assets/bg/paralelcourse.jpg";

const containerVariants = {
  hidden: { opacity: 0 },
  visible: { opacity: 1, transition: { staggerChildren: 0.15 } },
};

const courseVariants = {
  hidden: { opacity: 0, y: 40, scale: 0.95 },
  visible: {
    opacity: 1,
    y: 0,
    scale: 1,
    transition: { type: "spring", stiffness: 100, damping: 18 },
  },
  hover: {
    y: -6,
    scale: 1.03,
    boxShadow:
      "0 15px 25px -5px rgba(255,255,255,0.2), 0 8px 8px -5px rgba(0,0,0,0.1)",
    transition: { type: "spring", stiffness: 300, damping: 20 },
  },
};

export default function Courses() {
  const [courses, setCourses] = useState([]);
  const ref = useRef(null);
  const controls = useAnimation();
  const inView = useInView(ref, { threshold: 0.2 });

  useEffect(() => {
    if (inView) controls.start("visible");
    else controls.start("hidden");
  }, [controls, inView]);

  const { scrollYProgress } = useScroll({
    target: ref,
    offset: ["start end", "end start"],
  });

  const backgroundY = useTransform(scrollYProgress, [0, 1], [0, -180]);
  const contentY = useTransform(scrollYProgress, [0, 1], [0, 40]);

  useEffect(() => {
    let mounted = true;
    getCourses().then((data) => {
      if (mounted) setCourses(data || []);
    });
    return () => {
      mounted = false;
    };
  }, []);

  return (
    <section
      ref={ref}
      className="relative overflow-hidden"
      style={{ minHeight: "100vh", padding: "5rem 1rem" }}
      aria-label="படிப்புகள்"
    >
      {/* Background with parallax motion */}
      <motion.div
        className="absolute inset-0 bg-no-repeat bg-cover"
        style={{
          backgroundImage: `url(${bgImage})`,
          backgroundPosition: "top center",
          y: backgroundY,
          scale: 1.1,
        }}
        aria-hidden
      />

      {/* Light transparent overlay so text stays readable but image visible */}
      <div className="absolute inset-0 bg-white/10 backdrop-blur-[2px]" />

      {/* Foreground content */}
      <motion.div
        className="relative z-10 max-w-6xl mx-auto"
        style={{ y: contentY }}
        variants={containerVariants}
        initial="hidden"
        animate={controls}
      >
        <motion.h2
          className="text-4xl md:text-5xl font-bold mb-10 text-left text-white drop-shadow-lg"
          initial={{ opacity: 0, y: 12 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          படிப்புகள்
        </motion.h2>

        {/* Cards — glassmorphism style */}
        <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3 justify-start items-start">
          {courses.length === 0 ? (
            <div className="col-span-full text-center text-white">
              படிப்புகள் ஏற்றப்படுகிறது...
            </div>
          ) : (
            courses.map((c, i) => (
              <motion.article
                key={c.id ?? i}
                className="p-5 rounded-2xl border border-white/30 bg-white/20 backdrop-blur-md text-white shadow-md hover:shadow-xl transition-all"
                variants={courseVariants}
                whileHover="hover"
                whileTap={{ scale: 0.97 }}
              >
                <motion.h3
                  className="text-lg font-semibold text-white"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: i * 0.05 }}
                >
                  {c.title}
                </motion.h3>
                <motion.p
                  className="mt-2 text-gray-100 text-sm leading-relaxed"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: i * 0.05 + 0.1 }}
                >
                  {c.desc}
                </motion.p>
                <motion.div
                  className="mt-3 flex justify-end"
                  initial={{ opacity: 0, x: -10 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: i * 0.05 + 0.2 }}
                >
                  <button className="px-3 py-1.5 bg-blue-600/80 text-white text-sm rounded-lg hover:bg-blue-700 transition-colors">
                    மேலும் அறிக
                  </button>
                </motion.div>
              </motion.article>
            ))
          )}
        </div>
      </motion.div>
    </section>
  );
}
