import React from 'react';
import { motion } from 'framer-motion';
import heroImg from '../assets/hero.jpg';

export default function Hero(){
  return (
    <section className="relative">
      <div className="h-72 md:h-96 bg-gradient-to-r from-indigo-600 to-purple-600 flex items-center">
        <div className="container mx-auto px-4 text-white">
          <motion.h1 initial={{opacity:0, y:20}} animate={{opacity:1, y:0}} transition={{duration:0.6}} className="text-3xl md:text-5xl font-extrabold">லயரிதம்</motion.h1>
          <motion.p initial={{opacity:0}} animate={{opacity:1}} transition={{delay:0.2}} className="mt-4 max-w-xl">கர்நாடக இசையின் அறிமுகம் — மெல்லிசைகள் மற்றும் சிக்கலான தாளங்களுக்கு பெயர் பெற்றது.</motion.p>
          <div className="mt-6">
            <a className="inline-block bg-white text-indigo-600 font-semibold px-6 py-2 rounded shadow hover:scale-105 transition-transform" href="#">இப்போது விண்ணப்பிக்கவும்</a>
          </div>
        </div>
      </div>
    </section>
  );
}
