import React from "react";
import { motion } from "framer-motion";
import { Player } from "@lottiefiles/react-lottie-player";
import animationData from "../assets/Core-Apps Associations.json"; // Your Lottie JSON file

// Import your images from src/assets/events
import mirudangamImg from "../assets/events/mridungamshow.png";
import veenaImg from "../assets/events/veenashow.png";
import sangeethamImg from "../assets/events/sethupathishow.png";

const events = [
  {
    id: 1,
    title: "மிருதங்கக் கச்சேரி",
    description: "அற்புதமான மிருதங்க இசை நிகழ்ச்சி.",
    bgImage: mirudangamImg,
  },
  {
    id: 2,
    title: "வீணைக் கச்சேரி",
    description: "சங்கீத விருப்பங்களுக்கான வீணைக் கச்சேரி.",
    bgImage: veenaImg,
  },
  {
    id: 3,
    title: "சேதுபதி இசை நிகழ்ச்சி",
    description: "நிகழ்ச்சியின் அழகான இசை நிகழ்ச்சி.",
    bgImage: sangeethamImg,
  },
];

export default function Events() {
  return (
    <section className="py-12 bg-gray-100">
      <div className="container mx-auto px-4">
        {/* Header with Animation */}
        <div className="flex flex-col lg:flex-row items-center justify-center gap-8 mb-12">
          {/* Lottie Animation - Left side on large screens */}
          <motion.div
            className="w-48 h-48 lg:w-64 lg:h-64 flex-shrink-0"
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            <Player
              autoplay
              loop
              src={animationData}
              style={{ height: '100%', width: '100%' }}
              renderer="svg" // Use SVG for crisp quality
              rendererSettings={{
                preserveAspectRatio: 'xMidYMid meet',
              }}
            />
          </motion.div>

          {/* Heading - Right side on large screens */}
          <motion.h2
            className="text-4xl lg:text-5xl font-bold text-center leading-tight"
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.4 }}
          >
            நிகழ்வுகள்
            <span className="text-2xl block text-blue-600 mt-2"></span>
          </motion.h2>
        </div>

        {/* Events Grid */}
        <div className="grid md:grid-cols-3 gap-6">
          {events.map((event) => (
            <motion.div
              key={event.id}
              className="p-6 rounded-lg shadow-lg relative overflow-hidden group"
              style={{
                backgroundImage: `url(${event.bgImage})`,
                backgroundSize: "cover",
                backgroundPosition: "center",
                minHeight: "200px",
              }}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5 }}
              whileHover={{ scale: 1.02 }}
            >
              {/* Overlay */}
              <div className="absolute inset-0 bg-black bg-opacity-40 rounded-lg group-hover:bg-opacity-50 transition-all duration-300"></div>

              <motion.div className="relative z-10 h-full flex flex-col justify-end">
                <motion.h3
                  className="text-xl font-semibold mb-2 text-white"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 0.2 }}
                >
                  {event.title}
                </motion.h3>
                <motion.p
                  className="text-white text-base leading-relaxed"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 0.4 }}
                >
                  {event.description}
                </motion.p>
              </motion.div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
}
