import React, {useEffect, useState} from 'react';
import { getEvents } from '../mockData';
import { motion, useInView, useAnimation } from 'framer-motion';
import axios from 'axios';

const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.1,
      delayChildren: 0.3
    }
  }
};

const eventCardVariants = {
  hidden: { 
    opacity: 0,
    y: 50,
    scale: 0.9
  },
  visible: {
    opacity: 1,
    y: 0,
    scale: 1,
    transition: {
      type: "spring",
      stiffness: 100,
      damping: 20
    }
  },
  hover: {
    scale: 1.05,
    y: -10,
    boxShadow: "0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)",
    transition: {
      type: "spring",
      stiffness: 400,
      damping: 25
    }
  }
};

export default function Events(){
  const [events, setEvents] = useState([]);
  useEffect(()=>{ getEvents().then(setEvents) }, []);
  return (
    <section className="mt-12">
      <h2 className="text-2xl font-bold mb-4">நிகழ்வுகள்</h2>
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {events.map(ev=>(
          <motion.div key={ev.id} className="p-4 bg-white rounded-lg shadow" whileHover={{scale:1.02}}>
            <h4 className="font-semibold">{ev.title}</h4>
            <div className="text-xs text-gray-500">{ev.date}</div>
            <p className="mt-2 text-sm">{ev.desc}</p>
          </motion.div>
        ))}
      </div>
    </section>
  );
}
