import React, { useEffect, useState, useRef } from "react";
import { getCourses } from "../mockData";
import {
  motion,
  useAnimation,
  useInView,
  useScroll,
  useTransform
} from "framer-motion";
import bgImage from "../assets/bg/paralelcourse.jpg"; // update if needed

const containerVariants = {
  hidden: { opacity: 0 },
  visible: { opacity: 1, transition: { staggerChildren: 0.18 } },
};

const courseVariants = {
  hidden: { opacity: 0, y: 40, scale: 0.98 },
  visible: {
    opacity: 1,
    y: 0,
    scale: 1,
    transition: { type: "spring", stiffness: 110, damping: 20, duration: 0.6 },
  },
  hover: {
    y: -8,
    scale: 1.02,
    boxShadow:
      "0 20px 25px -5px rgba(0,0,0,0.1), 0 10px 10px -5px rgba(0,0,0,0.04)",
    transition: { type: "spring", stiffness: 400, damping: 25 },
  },
};

export default function Courses() {
  const [courses, setCourses] = useState([]);
  const ref = useRef(null);

  // controls for cards entrance
  const controls = useAnimation();
  const inView = useInView(ref, { threshold: 0.18 });

  useEffect(() => {
    if (inView) controls.start("visible");
    else controls.start("hidden");
  }, [controls, inView]);

  // USE useScroll targeted at the section ref — much more reliable
  const { scrollYProgress } = useScroll({
    target: ref,
    offset: ["start end", "end start"], // element enters -> 0, leaves -> 1
  });

  // translate progress [0..1] to pixel offset for background and subtle heading
  // background moves up to -180px while section is visible
  const backgroundY = useTransform(scrollYProgress, [0, 1], [0, -180]);
  // subtle opposite movement for cards to add depth (optional)
  const contentY = useTransform(scrollYProgress, [0, 1], [0, 40]);

  useEffect(() => {
    let mounted = true;
    getCourses().then((data) => {
      if (mounted) setCourses(data || []);
    });
    return () => {
      mounted = false;
    };
  }, []);

  return (
    <section
      ref={ref}
      className="relative overflow-hidden"
      style={{ minHeight: "100vh", padding: "4rem 1rem" }}
      aria-label="படிப்புகள்"
    >
      {/* Parallax background — top aligned so top of image shows first */}
      <motion.div
        className="absolute inset-0 bg-no-repeat bg-cover"
        style={{
          backgroundImage: `url(${bgImage})`,
          backgroundPosition: "top center",
          y: backgroundY,
          scale: 1.08,
        }}
        aria-hidden
      />

      {/* Gentle gradient overlay so image remains visible but text is readable */}
      <div className="absolute inset-0 bg-gradient-to-b from-white/10 via-white/30 to-white/70" />

      {/* Content container — moves slightly for layered parallax */}
      <motion.div
        className="relative z-10 max-w-6xl mx-auto"
        style={{ y: contentY }}
        variants={containerVariants}
        initial="hidden"
        animate={controls}
      >
        <motion.h2
          className="text-4xl md:text-5xl font-bold mb-10 text-center text-gray-900"
          initial={{ opacity: 0, y: 12 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          படிப்புகள்
        </motion.h2>

        <div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3">
          {courses.length === 0 ? (
            <div className="col-span-full text-center text-gray-700">
              படிப்புகள் ஏற்றப்படுகிறது...
            </div>
          ) : (
            courses.map((c, i) => (
              <motion.article
                key={c.id ?? i}
                className="p-6 bg-white rounded-2xl shadow-lg border border-white/60"
                variants={courseVariants}
                whileHover="hover"
                whileTap={{ scale: 0.98 }}
              >
                <motion.h3
                  className="text-xl font-semibold text-gray-900"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: i * 0.06 }}
                >
                  {c.title}
                </motion.h3>
                <motion.p
                  className="mt-3 text-gray-700"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: i * 0.06 + 0.14 }}
                >
                  {c.desc}
                </motion.p>
                <motion.div
                  className="mt-4 flex justify-end"
                  initial={{ opacity: 0, x: -12 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: i * 0.06 + 0.28 }}
                >
                  <button className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors">
                    மேலும் அறிக
                  </button>
                </motion.div>
              </motion.article>
            ))
          )}
        </div>
      </motion.div>
    </section>
  );
}
